    private void perimetroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_perimetroActionPerformed
        String sele= (String)Options.getSelectedItem();
        int p1;
        int p2;
        try{
            p1= Integer.parseInt(param1.getText());
            if (sele =="Circulo"){
                Circulo c= new Circulo(p1);
                perimetroLbl.setText(Double.toString(c.calcularPerimetro()));
                
            }else if(sele== "Rectangulo"){
                p2= Integer.parseInt(param2.getText());
                Rectangulo c= new Rectangulo(p1, p2);
                perimetroLbl.setText(Double.toString(c.calcularPerimetro()));
    
            }else if(sele=="Cuadrado"){
                Cuadrado c= new Cuadrado(p1);
                perimetroLbl.setText(Double.toString(c.calcularPerimetro()));
 
            }else if(sele=="Triangulo Rectangulo"){
                p2= Integer.parseInt(param2.getText());
                TrianguloRectangulo c= new TrianguloRectangulo(p1, p2);
                perimetroLbl.setText(Double.toString(c.calcularPerimetro()));
            }
        }catch(Exception err){
            errorLbl.setText(err.toString());
            ErrorDialog.show(true);
        }
    }//GEN-LAST:event_perimetroActionPerformed

