    private void TipoTrianguloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_TipoTrianguloActionPerformed
        int p1;
        int p2;
        try{
            p1= Integer.parseInt(param1.getText());
            p2= Integer.parseInt(param2.getText());
            TrianguloRectangulo c= new TrianguloRectangulo(p1, p2);
            tipoTLabel.setText(c.determinarTipoTriangulo());            
        }catch(Exception err){
            ErrorDialog.show(true);
        }
    }//GEN-LAST:event_TipoTrianguloActionPerformed

    private void OptionsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_OptionsActionPerformed
        String sele= (String)Options.getSelectedItem();
        try{
            param1.setText("");
            param2.setText("");
            if (sele =="Circulo"){
                param2.setVisible(false);
                perimetroLbl.setText("Perimetro del Circulo");
                areaLbl.setText("Area del Circulo");
                paramLbl.setText("Inserte el radio del Circulo:");
                
            }else if(sele== "Rectangulo"){
                perimetroLbl.setText("Perimetro del Rectangulo");
                areaLbl.setText("Area del Rectangulo");
                paramLbl.setText("Inserte base y altura:");
                param2.setVisible(true);
                
            }else if(sele=="Cuadrado"){
                param2.setVisible(false);
                perimetroLbl.setText("Perimetro del Cuadrado");
                areaLbl.setText("Area del Cuadrado");
                paramLbl.setText("Inserte el lado del Cuadrado:");
                
            }else if(sele=="Triangulo Rectangulo"){
                perimetroLbl.setText("Perimetro del Triangulo");
                areaLbl.setText("Area del Triangulo");
                tipoTLabel.setText("Tipo de Triangulo");
                hipotenusaLbl.setText("Hipotenusa del triangulo");
                paramLbl.setText("Inserte base y altura:");
                param2.setVisible(true);
                TipoTriangulo.setEnabled(true);
                hipotenusa.setEnabled(true);
            }
            
        }catch(Exception error){
            errorLbl.setText(error.toString());
            ErrorDialog.show(true);
        }
    }//GEN-LAST:event_OptionsActionPerformed

